import React from 'react';
import axios from 'axios';
import Card from '../components/Card';
import Flex from '../components/Flex';
import Input from '../components/InputComponent';
import Spinner from '../components/Spinner';
import Styled from 'styled-components';
import Text from '../components/Typography';

const Header = Styled.div`
position: fixed;
box-sizing: border-box;
width: 100%;
z-index: 1;
background: #1e78bf;
padding: 22px;
top: 0px;

`;
const InputStyled = Styled.div`
display: flex;
z-index: 1;
> input, > textarea {
  font-size: 16px;
  font-weight: 500;
  padding: 12px 14px;
  border: 1px solid #ffffff;
  border-right-width: 0px;
  border-radius: 6px 0px 0px 6px;
  width: 100%;
  color: #ffffff;
  outline: none;
  background: none;
}
>input::placeholder, >textarea::placeholder{
    color: rgba(18, 18, 29, 0.3);
  }

}`;

const Button = Styled.button`
background: none;
color: #ffffff;
border: 1px solid #ffffff;
padding: 8px 12px;
border-radius: 0px 6px 6px 0px;
outline: none;
cursor: pointer;
`;

const LoadButton = Styled.button`
background: none;
color: #ffffff;
border: 1px solid #ffffff;
padding: 8px 12px;
border-radius: 6px;
outline: none;
cursor: pointer;
`;
const AppStyled = Styled.div`
width: 100vw;
height: 100vh;
`;

const App: React.FC = () => {
  const [input, setInput] = React.useState('');
  const [detail, setDetail] = React.useState([]);
  const [page, setPage] = React.useState(1);
  const [isLoading, setIsLoading] = React.useState(false);

  const handleOnSearch = async (input: string) => {
    setIsLoading(true);
    try {
      const { data } = await axios({
        url: `https://api.jikan.moe/v3/search/anime?q=${input}&limit=16&page=${page}`,
        method: 'get',
      });
      setIsLoading(false);
      if (detail.length) {
        var more: any = [...detail, ...data.results];
        setDetail(more);
      } else setDetail(data.results);
    } catch (error) {}
  };
  return (
    <AppStyled>
      <Card background="#1E78BF" width="100%" padding="0px">
        <Header>
          <InputStyled>
            <Input
              fontSize="h4"
              fontWeight="semiBold"
              onChange={(e) => {
                setInput(e.target.value);
              }}
              onKeyUp={(e) => {
                if (e.keyCode === 13) {
                  handleOnSearch(input);
                }
              }}
              placeholder="Search here..."
              value={input}
            />
            <Button onClick={() => handleOnSearch(input)}> Go</Button>
          </InputStyled>
          <Flex margin="12px 0px">
            <Text fontSize="h5" fontWeight="regular" stringColor="#c6c6c6">
              Requesting:&nbsp;
              <span style={{ color: '#ffffff' }}>
                {' '}
                https://api.jikan.moe/v3/search/anime?q=&limit=16&page=
              </span>
            </Text>
          </Flex>
        </Header>
        {!isLoading ? (
          <>
            {detail.length ? (
              <>
                <Flex
                  justifyContent="space-evenly"
                  flexWrap="wrap"
                  padding="184px 0px 0px 0px"
                  alignItem="stretch"
                >
                  {detail.map((el: any, index) => {
                    return (
                      <Card
                        padding="0px"
                        margin="0px 22px 22px 0px"
                        width="225px"
                        borderRadius="6px"
                        cursor="pointer"
                        onClick={() => (window.location.href = el.url)}
                        key={index}
                      >
                        <img
                          src={el.image_url}
                          style={{ borderRadius: '6px 6px 0px 0px' }}
                          width="225px"
                          height="326px"
                          alt="titleImage"
                        />
                        <Flex justifyContent="center" padding="12px 0px">
                          <Text
                            fontSize="h6"
                            fontWeight="semiBold"
                            stringColor="#000"
                            textAlign="center"
                            // lineClamp={1}
                            padding="0px 12px"
                          >
                            {el.title}
                          </Text>
                        </Flex>
                      </Card>
                    );
                  })}
                </Flex>
                <Flex justifyContent="center" padding="0px 0px 21px 0px">
                  <LoadButton
                    onClick={() => {
                      setPage(page + 1);
                      handleOnSearch(input);
                    }}
                  >
                    Load More
                  </LoadButton>
                </Flex>
              </>
            ) : (
              <Flex
                flexDirection="column"
                padding="184px 0px 0px 0px"
                justifyContent="center"
              >
                <img
                  src="/image.jpg"
                  style={{ margin: '0px 0px 12px 0px' }}
                  alt="baseImage"
                />
                <Text fontSize="h6" fontWeight="bold" stringColor="#ffffff">
                  Welcome to the Anime Land
                </Text>
              </Flex>
            )}
          </>
        ) : (
          <Flex
            justifyContent="center"
            padding="184px 0px 0px 0px"
            width="100%"
          >
            <Spinner type="primary" />
          </Flex>
        )}
      </Card>
    </AppStyled>
  );
};

export default App;
